%angular
<style>
    .game-container { display: flex; gap: 20px; padding: 10px; font-family: Arial; user-select: none; }
    .board-view {
        position: relative; width: 400px; height: 400px;
        background: linear-gradient(135deg, #DEB887, #D2B48C);
        border: 10px solid #8B4513; border-radius: 8px;
        box-shadow: inset 0 0 20px rgba(0,0,0,0.3);
        outline: none;
    }
    .board-view:focus { border-color: #28a745; box-shadow: 0 0 0 4px rgba(40,167,69,0.6), inset 0 0 20px rgba(0,0,0,0.3); }
    .ball {
        position: absolute; width: 24px; height: 24px;
        background: radial-gradient(circle at 30% 30%, #fff, #aaa, #555);
        border-radius: 50%; transform: translate(-50%, -50%);
        box-shadow: 3px 3px 6px rgba(0,0,0,0.4);
    }
    .goal {
        position: absolute; width: 48px; height: 48px;
        background: radial-gradient(circle, #000 40%, #222 60%, #000);
        border-radius: 50%; transform: translate(-50%, -50%);
    }
    .goal::after {
        content: ''; position: absolute; top: 50%; left: 50%;
        width: 24px; height: 24px;
        background: radial-gradient(circle, #0f0, #080);
        border-radius: 50%; transform: translate(-50%, -50%);
        animation: pulse 1s infinite;
    }
    @keyframes pulse { 0%,100%{opacity:1;transform:translate(-50%,-50%) scale(1);} 50%{opacity:0.6;transform:translate(-50%,-50%) scale(0.7);} }
    .wall {
        position: absolute;
        background: linear-gradient(180deg, #A0522D, #8B4513);
        border: 2px solid #5D3A1A; border-radius: 3px;
    }
    .hole {
        position: absolute;
        background: radial-gradient(circle, #000 60%, #333);
        border-radius: 50%; transform: translate(-50%, -50%);
        box-shadow: inset 0 0 10px #000;
    }
    .coin {
        position: absolute; width: 20px; height: 20px;
        background: radial-gradient(circle at 30% 30%, #ffd700, #daa520, #b8860b);
        border-radius: 50%; transform: translate(-50%, -50%);
        box-shadow: 0 0 8px #ffd700;
    }
    .controls { padding: 15px; background: #f8f9fa; border-radius: 8px; min-width: 260px; }
    .hud { padding: 12px; background: #1a1a1a; color: #0f0; font-family: 'Courier New', monospace; border-radius: 6px; margin-bottom: 15px; font-size: 14px; }
    .direction-pad { display: grid; grid-template-columns: repeat(3, 60px); gap: 5px; justify-content: center; }
    .dir-btn {
        width: 60px; height: 60px; font-size: 22px; font-weight: bold;
        cursor: pointer; border: 2px solid #444; border-radius: 8px;
        background: linear-gradient(180deg, #fff, #ddd);
    }
    .dir-btn:hover { background: linear-gradient(180deg, #e0f0ff, #b8d4f0); border-color: #007bff; }
    .dir-btn:active { background: #28a745 !important; color: white !important; transform: scale(0.92); }
    .status-success { color: #0f0; font-size: 20px; font-weight: bold; }
    .status-failed { color: #f00; font-size: 20px; font-weight: bold; }
    .hint { margin-top:12px; padding:10px; background:#e9ecef; border-radius:4px; font-size:13px; line-height: 1.5; }
</style>

<div class="game-container">
    <div class="board-view" tabindex="0"
         ng-keydown="
            $event.preventDefault();
            (($event.key=='w'||$event.key=='W'||$event.key=='ArrowUp') && z.runParagraph('para_up'));
            (($event.key=='s'||$event.key=='S'||$event.key=='ArrowDown') && z.runParagraph('para_down'));
            (($event.key=='a'||$event.key=='A'||$event.key=='ArrowLeft') && z.runParagraph('para_left'));
            (($event.key=='d'||$event.key=='D'||$event.key=='ArrowRight') && z.runParagraph('para_right'));
            (($event.key=='q'||$event.key=='Q') && z.runParagraph('para_upleft'));
            (($event.key=='e'||$event.key=='E') && z.runParagraph('para_upright'));
            (($event.key=='z'||$event.key=='Z') && z.runParagraph('para_downleft'));
            (($event.key=='c'||$event.key=='C') && z.runParagraph('para_downright'));
            (($event.key=='r'||$event.key=='R') && z.runParagraph('para_reset'));
         ">
        <!-- Goal -->
        <div class="goal" ng-repeat="e in state.entities" ng-if="e.type=='GOAL'"
             ng-style="{left: (200 + e.components.TransformComponent.position[0] * 50) + 'px', 
                        top: (200 - e.components.TransformComponent.position[1] * 50) + 'px'}"></div>
        <!-- Walls -->
        <div class="wall" ng-repeat="e in state.entities" ng-if="e.type=='WALL'"
             ng-style="{left: (200 + e.components.TransformComponent.position[0]*50 - e.components.TransformComponent.scale[0]*25) + 'px',
                        top: (200 - e.components.TransformComponent.position[1]*50 - e.components.TransformComponent.scale[1]*25) + 'px',
                        width: e.components.TransformComponent.scale[0]*50 + 'px',
                        height: e.components.TransformComponent.scale[1]*50 + 'px'}"></div>
        <!-- Holes -->
        <div class="hole" ng-repeat="e in state.entities" ng-if="e.type=='HOLE'"
             ng-style="{left: (200 + e.components.TransformComponent.position[0]*50) + 'px',
                        top: (200 - e.components.TransformComponent.position[1]*50) + 'px',
                        width: e.components.TransformComponent.scale[0]*50 + 'px',
                        height: e.components.TransformComponent.scale[1]*50 + 'px'}"></div>
        <!-- Coins -->
        <div class="coin" ng-repeat="e in state.entities" ng-if="e.type=='COIN'"
             ng-style="{left: (200 + e.components.TransformComponent.position[0]*50) + 'px',
                        top: (200 - e.components.TransformComponent.position[1]*50) + 'px'}"></div>
        <!-- Ball -->
        <div class="ball" ng-if="ball.position"
             ng-style="{left: (200 + ball.position[0] * 50) + 'px', top: (200 - ball.position[1] * 50) + 'px'}"></div>
    </div>

    <div class="controls">
        <div class="hud">
            <div>Score: {{ball.inventory.total_score || 0}}</div>
            <div>Steps: {{step_count}}</div>
            <div ng-class="{'status-success': status=='SUCCESS!', 'status-failed': status=='FAILED!'}">{{status}}</div>
        </div>

        <div class="direction-pad">
            <button class="dir-btn" ng-click="z.runParagraph('para_upleft')">&#8598;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_up')">&#9650;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_upright')">&#8599;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_left')">&#9664;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_reset')" style="background:#ffc107;">&#8635;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_right')">&#9654;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_downleft')">&#8601;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_down')">&#9660;</button>
            <button class="dir-btn" ng-click="z.runParagraph('para_downright')">&#8600;</button>
        </div>

        <div class="hint">
            <b>Click board first!</b><br>
            WASD / Arrows = Move<br>
            Q/E/Z/C = Diagonal<br>
            R = Reset<br>
            <b>Goal:</b> Reach green hole!
        </div>
    </div>
</div>
